import React from 'react'
import ReactDOM from 'react-dom'

const Otsikko = (props) => {
    return (
        <div>
            <h1>{props.kurssi}</h1>
        </div>
    )

}

const Sisalto = (props) => {
    console.log(props)

    return (
        <div>
            <Osa osa={osat[0]} />
            <Osa osa={osat[1]} />
            <Osa osa={osat[2]} />
        </div>
    )
}

const Osa = (props) => {
    return (
        <div>
            <p>{props.osa.kurssi}</p>
        </div>
    )
}

const Yhteensa = (props) => {
    let yhteensa = 0
    props.osat.map(prop => (
        yhteensa += prop.tehtavia
    ))

    return (  
      <div>
        <p>Yhteensä {yhteensa}</p>
      </div>
  )
}

const App = () => {
  const kurssi = 'Half Stack -sovelluskehitys'
  const osat = [
      {osa:'Reactin perusteet', tehtavia:10},
      {osa:'Tiedonvälitys propseilla', tehtavia:7},
      {osa:'Komponenttien tila', tehtavia:14}
  ];

  return (
    <div>
      <Otsikko kurssi={kurssi} />
      <Sisalto  osat={osat}/>
      <Yhteensa osat={osat} />
    </div>
  )
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
)
